import React from 'react';

import { createNativeStackNavigator } from '@react-navigation/native-stack';
import HomeScreen from './HomeScreen';
import LoginScreen from './LoginScreen';
import RegisterScreen from './RegisterScreen';
import Perfil from './Perfil'; // Tela de registro, que você deve implementar.

const Stack = createNativeStackNavigator();

const Conta = () => {

  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => { // Use onAuthStateChanged do auth
      setUser(user);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  if (loading) {
    return <Text>Loading...</Text>;
  }

  if (!user) {
    navigation.navigate('Login'); // Redireciona para a tela de login
    return null; // Previne renderização da tela se o usuário não estiver logado
  }

  return (

      <Stack.Navigator initialRouteName="Home">
        <Stack.Screen name="Home" component={HomeScreen} options={{headerShown: false}}/>
        <Stack.Screen name="Login" component={LoginScreen} options={{headerShown: false}}/>
        <Stack.Screen name="Register" component={RegisterScreen} options={{headerShown: false}}/>
        <Stack.Screen name="Perfil" component={Perfil} options={{headerShown: false}}/>
      </Stack.Navigator>

  );
};

export default Conta;
