import React, { useState, useEffect } from 'react';
import { View, FlatList, Text, RefreshControl } from 'react-native';
import { firestore } from '../firebase/config'; // Certifique-se de que a importação está correta
import { collection, getDocs } from 'firebase/firestore';

const Feed = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchPosts = async () => {
    setLoading(true);
    try {
      const querySnapshot = await getDocs(collection(firestore, 'posts'));
      const postsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setPosts(postsData);
    } catch (error) {
      console.error('Erro ao buscar posts: ', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPosts(); // Chama a função para buscar posts quando o componente é montado
  }, []);

  const onRefresh = () => {
    fetchPosts(); // Recarrega os posts quando o usuário arrasta para baixo
  };

  return (
    <View style={{ flex: 1 }}>
      <FlatList
        data={posts}
        keyExtractor={item => item.id}
        renderItem={({ item }) => (
          <View style={{ padding: 20 }}>
            <Text style={{ fontSize: 16, fontWeight: 'bold' }}>{item.texto}</Text>
            {/* Renderize outros detalhes do post aqui */}
          </View>
        )}
        refreshControl={
          <RefreshControl
            refreshing={loading}
            onRefresh={onRefresh}
          />
        }
      />
    </View>
  );
};

export default Feed;
