import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, FlatList, TouchableOpacity, StyleSheet } from 'react-native';
import { collection, query, where, getDocs } from 'firebase/firestore';
import { firestore } from '../firebase/config'; // Certifique-se de importar corretamente o Firestore

const SearchUsers = () => {
  const [searchText, setSearchText] = useState('');
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (searchText.trim() === '') {
      setUsers([]); // Limpa os resultados quando não há texto
      return;
    }

    // Adiciona um delay antes de fazer a busca para evitar muitas consultas
    const delayDebounceFn = setTimeout(() => {
      handleSearch(); // Executa a busca após o delay
    }, 500); // 500ms de delay

    // Limpa o timeout se o usuário continuar digitando
    return () => clearTimeout(delayDebounceFn);
  }, [searchText]);

  const handleSearch = async () => {
    setLoading(true);
    setUsers([]); // Limpa os resultados anteriores

    try {
      // Busca por nome ou username
      const userQuery = query(
        collection(firestore, 'users'),
        where('nome', '>=', searchText.toLowerCase()),
        where('nome', '<=', searchText.toLowerCase() + '\uf8ff') // Limita o resultado para correspondências completas
      );

      const usernameQuery = query(
        collection(firestore, 'users'),
        where('username', '>=', searchText.toLowerCase()),
        where('username', '<=', searchText.toLowerCase() + '\uf8ff')
      );

      const [nameSnapshot, usernameSnapshot] = await Promise.all([
        getDocs(userQuery),
        getDocs(usernameQuery),
      ]);

      // Unifica resultados e remove duplicatas
      const allResults = [...nameSnapshot.docs, ...usernameSnapshot.docs];
      const uniqueUsers = Array.from(
        new Set(allResults.map((doc) => doc.id))
      ).map((id) => {
        return allResults.find((doc) => doc.id === id).data();
      });

      setUsers(uniqueUsers);
    } catch (error) {
      console.error('Erro ao buscar usuários: ', error);
      alert('Erro ao buscar usuários.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Buscar Usuários</Text>

      <TextInput
        style={styles.input}
        placeholder="Nome ou Username"
        value={searchText}
        onChangeText={setSearchText}
        placeholderTextColor="#999"
      />

      {loading ? (
        <Text>Carregando...</Text>
      ) : (
        <FlatList
          data={users}
          keyExtractor={(item) => item.uid}
          renderItem={({ item }) => (
            <View style={styles.userItem}>
              <Text style={styles.userName}>{item.nome}</Text>
              <Text style={styles.userUsername}>@{item.username}</Text>
            </View>
          )}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#fff',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 10,
    padding: 10,
    marginBottom: 20,
    backgroundColor: '#f9f9f9',
  },
  userItem: {
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
    paddingVertical: 10,
  },
  userName: {
    fontSize: 18,
  },
  userUsername: {
    fontSize: 16,
    color: '#555',
  },
});

export default SearchUsers;
