import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, Image, ActivityIndicator, TouchableOpacity, Modal, TextInput, ScrollView } from 'react-native';
import { firestore, storage } from '../firebase/config';
import { collection, getDocs, query, where, doc, updateDoc, getDoc } from 'firebase/firestore';
import { auth } from '../firebase/config'; 
import { signOut } from 'firebase/auth';
import { Ionicons } from '@expo/vector-icons';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import * as ImagePicker from 'expo-image-picker';
import PostItem from './PostItem';


const ProfileScreen = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const [posts, setPosts] = useState([]);
  const [userId, setUserId] = useState(null);
  const [isModalVisible, setIsModalVisible] = useState(false); // Estado para controlar a visibilidade do modal
  const [editName, setEditName] = useState(''); // Estado para o nome editado
  const [editBio, setEditBio] = useState(''); // Estado para a bio editada
  const currentUser = auth.currentUser;

  useEffect(() => {
    const fetchUserData = async () => {
      if (currentUser) {
        try {
          const userQuery = query(
            collection(firestore, 'users'),
            where('uid', '==', currentUser.uid)
          );
          const userSnapshot = await getDocs(userQuery);

          userSnapshot.forEach((doc) => {
            const data = doc.data();
            setUserData({ id: doc.id, ...data });
            setEditName(data.nome); // Define o nome atual para edição
            setEditBio(data.bio); // Define a bio atual para edição
          });

          const postsQuery = query(
            collection(firestore, 'posts'),
            where('autor', '==', currentUser.uid)
          );
          const postSnapshot = await getDocs(postsQuery);

          const userPosts = await Promise.all(
            postSnapshot.docs.map(async (postDoc) => {
              const postData = { id: postDoc.id, ...postDoc.data() };
              if (postData.autor) {
                const userDocRef = doc(firestore, 'users', postData.autor);
                const userDoc = await getDoc(userDocRef);
                postData.autor = userDoc.exists()
                  ? { id: userDoc.id, ...userDoc.data() }
                  : null;
              }
              return postData;
            })
          );
          setPosts(userPosts);
        } catch (error) {
          console.error('Error fetching user data: ', error);
        } finally {
          setLoading(false);
        }
      } else {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [currentUser]);

  const handleLogout = async () => {
    try {
      setUserData(null);
      await signOut(auth);
    } catch (error) {
      alert('Erro', error.message);
    }
  };

  const handleSaveProfile = async () => {
    try {
      const userDocRef = doc(firestore, 'users', currentUser.uid);
      await updateDoc(userDocRef, { nome: editName, bio: editBio });
      setUserData((prevData) => ({ ...prevData, nome: editName, bio: editBio }));
      setIsModalVisible(false); // Fechar o modal após salvar
      alert('Perfil atualizado com sucesso!');
    } catch (error) {
      console.error('Erro ao atualizar perfil:', error);
      alert('Erro ao atualizar perfil.');
    }
  };

  if (loading) {
    return <ActivityIndicator size="large" color="#0000ff" />;
  }

  return (
    <View style={{ justifyContent: 'center', alignItems: 'center' }}>
      {userData ? (
        <>
          <Image
            source={userData.perfil ? { uri: userData.perfil } : require('../assets/profile.jpg')}
            style={{ width: 100, height: 100, borderRadius: 50, marginBottom: 10 }}
          />
          <Text style={{ fontSize: 20, fontWeight: 'bold' }}>{userData.nome}</Text>

          <TouchableOpacity onPress={pickImage}>
            <Text style={{ margin: 10, backgroundColor: '#DDA910', color: '#FFF', padding: 5, borderRadius: 15 }}>Alterar foto de perfil</Text>
          </TouchableOpacity>

          {uploading ? <ActivityIndicator size="large" color="#0000ff" /> : null}

          <TouchableOpacity
            style={{ margin: 10, backgroundColor: '#2499c7', padding: 1, paddingHorizontal: 10, borderRadius: 15 }}
            onPress={() => setIsModalVisible(true)} // Abre o modal ao clicar no botão
          >
            <Text style={{ color: '#FFF', fontSize: 18 }}>Editar Perfil</Text>
          </TouchableOpacity>

          <Modal
            visible={isModalVisible}
            transparent={true}
            animationType="slide"
            onRequestClose={() => setIsModalVisible(false)}
          >
            <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: 'rgba(0,0,0,0.5)' }}>
              <View style={{ width: 300, padding: 20, backgroundColor: 'white', borderRadius: 10 }}>
                <Text style={{ fontSize: 18, fontWeight: 'bold', marginBottom: 10 }}>Editar Perfil</Text>
                <TextInput
                  style={{ borderBottomWidth: 1, marginBottom: 10 }}
                  placeholder="Nome"
                  value={editName}
                  onChangeText={setEditName}
                />
                <TextInput
                  style={{ borderBottomWidth: 1, marginBottom: 20 }}
                  placeholder="Bio"
                  value={editBio}
                  onChangeText={setEditBio}
                />
                <TouchableOpacity
                  style={{ backgroundColor: '#2499c7', padding: 10, borderRadius: 10, marginBottom: 10 }}
                  onPress={handleSaveProfile}
                >
                  <Text style={{ color: '#FFF', textAlign: 'center' }}>Salvar</Text>
                </TouchableOpacity>
                <TouchableOpacity
                  style={{ backgroundColor: '#ef381e', padding: 10, borderRadius: 10 }}
                  onPress={() => setIsModalVisible(false)}
                >
                  <Text style={{ color: '#FFF', textAlign: 'center' }}>Cancelar</Text>
                </TouchableOpacity>
              </View>
            </View>
          </Modal>

          <View style={{ marginTop: 20, width: '100%' }}>
            <Text style={{ fontSize: 18, fontWeight: 'bold', marginBottom: 10, textAlign: 'center' }}>Posts</Text>
            <ScrollView style={{ height: 150 }}>
              <FlatList
                data={posts}
                renderItem={({ item }) => (
                  <PostItem
                    texto={item.texto}
                    img={item.img}
                    autor={item.autor}
                    id={item.id}
                    userId={userId}
                    dell={true}
                  />
                )}
                keyExtractor={(item) => item.id}
              />
            </ScrollView>
          </View>
        </>
      ) : (
        <Text>Erro no cadastro, exclua esta conta.</Text>
      )}
      <TouchableOpacity
        style={{ margin: 10, backgroundColor: '#ef381e', padding: 1, paddingHorizontal: 10, borderRadius: 15 }}
        onPress={handleLogout}
      >
        <Text style={{ color: '#FFF', fontSize: 17 }}>Sair</Text>
      </TouchableOpacity>
    </View>
  );
};

export default ProfileScreen;
