import React, { useState, useEffect } from 'react';
import { View, TextInput, FlatList, Text, StyleSheet } from 'react-native';
import { firestore } from '../firebase/config'; // ajuste o caminho conforme necessário
import { collection, query, where, onSnapshot } from 'firebase/firestore';

const Pesquisa = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const usersRef = collection(firestore, 'users'); // certifique-se de que 'users' é o nome da sua coleção
        const q = query(usersRef, where('username', '==', searchTerm));

        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const fetchedUsers = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setUsers(fetchedUsers);
        }, (error) => {
            console.error("Erro ao buscar usuários: ", error);
        });

        // Limpar a assinatura quando o componente for desmontado
        return () => unsubscribe();
    }, [searchTerm]);

    return (
        <View style={styles.container}>
            <TextInput
                style={styles.input}
                placeholder="Digite o nome de usuário..."
                value={searchTerm}
                onChangeText={setSearchTerm}
            />
            <FlatList
                data={users}
                keyExtractor={item => item.id}
                renderItem={({ item }) => (
                    <View style={styles.userItem}>
                        <Text>Nome: {item.name}</Text>
                        <Text>Usuário: {item.username}</Text>
                    </View>
                )}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 20,
        backgroundColor: '#fff',
    },
    input: {
        height: 40,
        borderColor: 'gray',
        borderWidth: 1,
        marginBottom: 10,
        paddingHorizontal: 10,
    },
    userItem: {
        padding: 15,
        borderBottomColor: '#ccc',
        borderBottomWidth: 1,
    },
});

export default Pesquisa;
