import React, { useEffect, useState } from 'react';
import { View, Text, ActivityIndicator } from 'react-native';
import auth from '../firebase/config';
import { createStackNavigator } from '@react-navigation/stack';


function Perfil() {
  const Stack = createStackNavigator();

  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Listener para mudanças no estado de autenticação
    const unsubscribe = auth().onAuthStateChanged((user) => {
      if (user) {
        setUser(user);  // Usuário logado
      } else {
        setUser(null); 
        navigation.navigate('LoginScreen')
      }
      setLoading(false); // Parar o carregamento após a verificação
    });

    // Desinscrever o listener quando o componente for desmontado
    return () => unsubscribe();
  }, []);

  if (loading) {
    return <ActivityIndicator size="large" color="#0000ff" />;
  }



  return (
    <NavigationContainer>
      <Stack.Navigator>
        <Stack.Screen
          name="Home"
          component={HomeScreen}
        />
        <Stack.Screen
          name="Profile"
          component={ProfileScreen}
          options={{ headerShown: false }}  // Esconde o cabeçalho da tela de perfil
        />
      </Stack.Navigator>
    </NavigationContainer>
  )
}

export default Perfil
