import React, { useState } from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  ActivityIndicator,
} from "react-native";
import { authentication } from "../firebase/config";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { useAuth } from "../contexts/AuthContext";
import { firestore } from '../firebase/config'; // Use a importação correta do Firestore
import { collection, query, where, getDocs, addDoc } from "firebase/firestore"; // Importações do Firestore

const SignUpScreen = ({ navigation }) => {
  const [nome, setNome] = useState("");
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [msgErro, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const { setLoggedInUser } = useAuth();

  const handleSignUp = async () => {
    if (password.length < 6) {
      setError('A senha deve ter no mínimo 6 caracteres.');
      return;
    }
    if (nome == "Alexandre de Morais"){
      navigation.navigate("Ban")
      return
    }
    
    setIsLoading(true);
    try {
      const usuarioRef = collection(firestore, 'users');
      const q = query(usuarioRef, where('username', '==', username));
      const snapshot = await getDocs(q);

      if (!snapshot.empty) {
        setError('Nome de usuário já existe!');
        setIsLoading(false);
        return;
      }

      // Criar usuário
      const res = await createUserWithEmailAndPassword(authentication, email, password);
      console.log(res.user);
      setLoggedInUser(res.user);

      // Adicionar usuário ao Firestore
      await addDoc(usuarioRef, {
        nome,
        username,
        email,
        uid: res.user.uid,
      });
      
    } catch (error) {
      setError('Erro ao realizar o cadastro: ' + error.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Sign Up</Text>
      <TextInput
        style={styles.input}
        placeholder="Nome de exibição"
        value={nome}
        onChangeText={setNome}
      />
      <TextInput
        style={styles.input}
        placeholder="Nome de Usuário"
        value={username}
        onChangeText={setUsername}
      />
      <TextInput
        style={styles.input}
        placeholder="Email"
        keyboardType="email-address"
        autoCapitalize="none"
        value={email}
        onChangeText={setEmail}
      />
      <TextInput
        style={styles.input}
        placeholder="Password"
        secureTextEntry
        value={password}
        onChangeText={setPassword}
      />
      <TextInput
        style={styles.input}
        placeholder="Confirm Password"
        secureTextEntry
        value={confirmPassword}
        onChangeText={setConfirmPassword}
      />
      {msgErro && <Text style={{ color: 'red' }}>{msgErro}</Text>}
      <TouchableOpacity onPress={handleSignUp} style={styles.button}>
        <Text style={styles.buttonText}>Cadastrar-se</Text>
        {isLoading && (
          <ActivityIndicator
            size="small"
            color="white"
            style={{ alignSelf: "center", justifyContent: "center", paddingLeft: 10 }}
          />
        )}
      </TouchableOpacity>
      <Text style={styles.downLoginText}>Já possui uma conta?</Text>
      <TouchableOpacity onPress={() => navigation.navigate("Login")}>
        <Text style={styles.login}>Entrar</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#ffffff",
    paddingHorizontal: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  input: {
    width: "100%",
    height: 40,
    borderColor: "#ccc",
    borderWidth: 1,
    borderRadius: 5,
    marginBottom: 16,
    paddingLeft: 8,
  },
  button: {
    backgroundColor: "#007BFF",
    width: "100%",
    paddingVertical: 15,
    borderRadius: 10,
    marginTop: 10,
  },
  buttonText: {
    color: "#ffffff",
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },
  login: {
    alignSelf: "flex-start",
    textDecorationLine: "underline",
    color: "#331ece",
    fontSize: 16,
    fontWeight: "500",
    marginLeft: 5,
    marginTop: 10,
  },
  downLoginText: {
    color: "#331ece",
    fontSize: 16,
    fontWeight: "400",
    marginTop: 10,
  }
});

export default SignUpScreen;
